current.google_font_styles:
.tt-tabs-default li > a {
    font-size: 16px;
}
.slider-revolution .tp-bullets {
    bottom: 20px !important;
    margin-left: 0 !important;
    left: auto !important;
    right: 20px;
}
.slider-revolution .tp-bullets .bullet {
    background-color: #ffffff;
    opacity: 0.5;
}
.slider-revolution .tp-bullets .bullet.selected {
    background-color: #ffffff;
    opacity: 1;
}
.slider-revolution.revolution-default .tp-leftarrow.default, .slider-revolution.revolution-default .tp-rightarrow.default{
display: none !important;
}
@media (min-width: 1230px){
  .slider-revolution .tp-caption1-wd-2{
    font-size: 40px;
    line-height: 44px;
  }
}
@media (min-width: 1025px) and (max-width: 1229px){
  .slider-revolution .tp-caption1-wd-2{
    font-size: 40px;
    line-height: 44px;
  }
}
@media (min-width: 790px) and (max-width: 1024px){
  .slider-revolution .tp-caption1-wd-2{
    font-size: 40px;
    line-height: 44px;
  }
}
.tt-layout-vertical .tt-img img,
.tt-product .tt-image-box img,
.tt-promo-box:not(.selecttext),
.slider-revolution{
    border-radius: 6px;
}
.tt-promo-box > *{
  display: block;
  overflow: hidden;
}
.tt-promo-box > img{
  -webkit-transition: -webkit-transform .5s;
  transition: transform 1s;
}
.tt-promo-box:hover > img {
  -ms-transform: scale3d(1.1,1.1,1);
  -webkit-transform: scale3d(1.1,1.1,1);
  -moz-transform: scale3d(1.1,1.1,1);
  transform: scale3d(1.1,1.1,1);
}
.icon-f-39:before {
    content: "\e98e";
}
.tt-product-single-info .btn.btn-lg .icon-shopping_basket:before,
.tt-product .tt-description .tt-btn-addtocart .icon-shopping_basket:before{
    content: "\e98e" !important;
}
.productandvideothumb .tt-btn-addtocart [class^="icon"]:before{
    top: -1px;
    content: "\e98e" !important;
}

current.popup_newsletter_custom_html:
